import React from 'react';
import { Link } from 'react-router-dom';
import '../App.css';

class Landing extends React.Component {
  state = { submit: false, jobs: [] };

  search = () => {
    var data = this.state.lang;
    axios
      .get('https://jobs.github.com/positions.json?description=' + data)
      .then((response) => {
        console.log(response.data[1].capital);
        if (response.status == 200) {
          this.setState({
            capital: response.data[1].capital,
            population: response.data[1].population,
            latlng: response.data[1].latlng,
            flag: response.data[1].flag,
            submit: true,
          });
        }
      })
      .catch((error) => {
        console.log(error);
      });
  };

  render() {
    return (
      <div className="container-fluid" id="landing">
        <div className="card">
          <div className="card-body">
            <div className="heading">
              what programming language youâ€™re looking for a job in.
            </div>
            <div class="form-group">
              <input
                type="text"
                class="form-control"
                placeholder="Enter language"
                onChange={(event) =>
                  this.setState({ lang: event.target.value })
                }
              />
            </div>
            <div className="form-group">
              <button
                type="submit"
                class="btn btn-primary"
                disabled={!this.state.lang}
                onClick={this.search}
              >
                Search
              </button>
            </div>
          </div>
        </div>
        <div className="card">
          <div className="card-body">
            {this.state.jobs ? <div></div> : <div>No Jobs Available</div>}
          </div>
        </div>
      </div>
    );
  }
}

export default Landing;
